con:config.script:
def project = testRunner.getTestCase().getTestSuite().getProject()
def authProfile = project.getAuthRepository().getEntry("ClientProfile")
def  uname = context.expand( '${Create Tenant#Response#$[\'app\'][\'client_id\']}' )
authProfile.setUsername(uname)
def pass = context.expand( '${Create Tenant#Response#$[\'app\'][\'client_secret\']}' )

authProfile.setPassword(pass)

authProfile.setPreemptive(true)

def username = context.expand( '${Create Tenant#Response#$[\'app\'][\'client_id\']}' )
def password = context.expand( '${Create Tenant#Response#$[\'app\'][\'client_secret\']}' )


def authString = "${username}:${password}"
def authBytes = authString.getBytes('UTF-8')
def encodedAuth = javax.xml.bind.DatatypeConverter.printBase64Binary(authBytes)

def authHeader = "Basic ${encodedAuth}"

log.info( authHeader)

//Store Client Profile Basic Authorization
def project = testRunner.testCase.testSuite.project
project.setPropertyValue("CLIENT AUTHORIZATION ID", authHeader)

//Store Tenant App id
def tenant_app_id = context.expand( '${Create Tenant#Response#$[\'app\'][\'client_id\']}' )
project.setPropertyValue("Tenant App id", tenant_app_id)

//Store Tenant id
def tenant_id = context.expand( '${Create Tenant#Response#$[\'id\']}' )
project.setPropertyValue("Tenant id", tenant_id)

def project = testRunner.getTestCase().getTestSuite().getProject()
def authProfile = project.getAuthRepository().getEntry("ClientProfile2")
def  uname = context.expand( '${Create Tenant2#Response#$[\'app\'][\'client_id\']}' )
authProfile.setUsername(uname)
def pass = context.expand( '${Create Tenant2#Response#$[\'app\'][\'client_secret\']}' )

authProfile.setPassword(pass)

authProfile.setPreemptive(true)

def username = context.expand( '${Create Tenant2#Response#$[\'app\'][\'client_id\']}' )
def password = context.expand( '${Create Tenant2#Response#$[\'app\'][\'client_secret\']}' )


def authString = "${username}:${password}"
def authBytes = authString.getBytes('UTF-8')
def encodedAuth = javax.xml.bind.DatatypeConverter.printBase64Binary(authBytes)

def authHeader = "Basic ${encodedAuth}"

log.info( authHeader)

//Store Client Profile Basic Authorization
def project = testRunner.testCase.testSuite.project
project.setPropertyValue("CLIENT2_AUTHORIZATION_ID", authHeader)

//Store Tenant App id
//def tenant_app_id = context.expand( '${Create Tenant2#Response#$[\'app\'][\'client_id\']}' )
//project.setPropertyValue("Tenant App id", tenant_app_id)

//Store Tenant id
//def tenant_id = context.expand( '${Create Tenant2#Response#$[\'id\']}' )
//project.setPropertyValue("Tenant id", tenant_id)

